            $start_page = max(1, $current_page - $half_visible);
            $end_page = min($total_pages, $start_page + $max_visible_pages - 1);
            if ($end_page - $start_page + 1 < $max_visible_pages) {
                $start_page = max(1, $end_page - $max_visible_pages + 1);
