<script>
   // Add this JavaScript to your main page
document.addEventListener('DOMContentLoaded', function() {
    const searchInput = document.getElementById('search-input');
    const contentGrid = document.querySelector('.content-grid');
    let searchTimeout;

    searchInput.addEventListener('input', function() {
        // Clear the previous timeout
        if (searchTimeout) {
            clearTimeout(searchTimeout);
        }

        // Set a new timeout to delay the search
        searchTimeout = setTimeout(function() {
            const query = searchInput.value.trim();
            
            // Show loading state
            contentGrid.style.opacity = '0.6';

            // Make the AJAX request
            fetch(`search.php?search=${encodeURIComponent(query)}`)
                .then(response => response.json())
                .then(data => {
                    contentGrid.innerHTML = data.html;
                    contentGrid.style.opacity = '1';
                    
                    // Update URL without refreshing the page
                    const url = new URL(window.location);
                    if (query) {
                        url.searchParams.set('search', query);
                    } else {
                        url.searchParams.delete('search');
                    }
                    window.history.pushState({}, '', url);
                })
                .catch(error => {
                    console.error('Search error:', error);
                    contentGrid.style.opacity = '1';
                });
        }, 300); // 300ms delay before searching
    });

    // Handle browser back/forward buttons
    window.addEventListener('popstate', function() {
        const url = new URL(window.location);
        const searchQuery = url.searchParams.get('search') || '';
        searchInput.value = searchQuery;
        
        if (searchQuery) {
            const event = new Event('input');
            searchInput.dispatchEvent(event);
        }
    });
});
</script>
